(789, 'Hãy Làm Cớm Nào', 'Let Be Cops', 'hay lam com nao', 'Jake Johnson, Damon Wayans Jr, Rob Riggle,', 'jake johnson, damon wayans jr, rob riggle,', 'https://lh6.googleusercontent.com/-fe3AYTF2hI0/VG-8SgvoE_I/AAAAAAAABPs/PWNB0y0PS3g/phim6v.com-poster.medium.jpg', '&lt;p&gt;\r\n	&lt;strong&gt;Ryan v&amp;agrave; Justin l&amp;agrave; hai anh ch&amp;agrave;ng độc th&amp;acirc;n vui t&amp;iacute;nh. Nhưng họ cảm thấy cuộc sống của m&amp;igrave;nh ng&amp;agrave;y c&amp;agrave;ng nh&amp;agrave;m ch&amp;aacute;n. Để tho&amp;aacute;t khỏi t&amp;igrave;nh cảnh ấy, hai anh ch&amp;agrave;ng bắt đầu một cuộc chơi mới. Khi tham dự lễ hội h&amp;oacute;a trang, họ đ&amp;atilde; chọn cho m&amp;igrave;nh bộ đồng phục cảnh s&amp;aacute;t. D&amp;ugrave; chỉ l&amp;agrave; cớm giả nhưng sau buổi tiệc ấy họ bỗng dưng được y&amp;ecirc;u mến v&amp;agrave; nổi tiếng. Tuy nhi&amp;ecirc;n, những rắc rối cũng bắt đầu từ đ&amp;oacute;...&lt;/strong&gt;&lt;/p&gt;\r\n', '5', 330, 0, '8', 'Luke Greenfield,', 'luke greenfield,', '104 Phút', '2014', '', 0, 0, 0, 0, 1, '0', '1416608811', 0, 'Hay Lam Com Nao, Let Be Cops', 'hay lam com nao, let be cops', 0, NULL, 'HD', 0, NULL, NULL, NULL, '17', NULL, '', '', NULL, NULL, 1, '', 0, 68, 164, '1', '0', '0', ''),
(791, 'Sự Thanh Trừng', 'The Purge: Anarchy', 'su thanh trung', 'Michael K. Williams, Zach Gilford, Frank Grillo,', 'michael k. williams, zach gilford, frank grillo,', 'http://3.bp.blogspot.com/-6ESUtq-_BFw/U-pJARUPnRI/AAAAAAAAB6U/N07yEsstbgU/s1600/xemphim74-su-thanh-trung-2.jpg', '<div style="text-align: justify;">\r\n	<b><span style="font-size: small;"><span style="font-family: Tahoma;">“The Purge: Anarchy”</span></span><span style="font-size: small;"><span style="font-family: Tahoma;"> có bối cảnh xảy ra sau những sự kiện của phần một, giữa một thời kỳ đen tối khi mà tội phạm lộng hành và chính phủ không còn khả năng duy trì trật tự an ninh xã hội. Thay vào đó, các nhà chức trách ban hành một chính sách kỳ lạ cho phép người dân có thể tự xử lý các hành vi tội phạm theo “luật rừng” trong một khoảng thời gian đã được ấn định từ trước. Tuy nhiên, điều này đã kéo theo hàng loạt sự kiện thanh trừng vô cùng khủng khiếp gây náo loạn, càng đẩy tình trạng vô chính phủ vượt ra ngoài tầm kiểm soát...</span></span></b></div>', '5', 217, 2, '3', 'James DeMonaco,', 'james demonaco,', '103 phút', '2014', '', 0, 0, 0, 0, 1, '', '1416622456', 0, 'Su Thanh Trung, The Purge: Anarchy', 'su thanh trung, the purge: anarchy', 0, NULL, 'HD', 0, NULL, NULL, NULL, '17', NULL, '', '', NULL, NULL, 1, '', 0, 22, 72, '', '0', '0', ''),
