(834, 'Naruto Shippuuden The Movie 1: Cái chết tiên đoán', 'Naruto Shippuuden The Movie 1', 'naruto shippuuden the movie 1: cai chet tien doan', 'Junko Takeuchi, Chie Nakamura, Noriaki Sugiyama', 'junko takeuchi, chie nakamura, noriaki sugiyama', 'http://4.bp.blogspot.com/-sJHe5a7KkPw/UeYaIwB0VHI/AAAAAAAANQI/h7TEj6Qww3Q/s1600/Naruto+Shippuuden+Movie+1+(2007)_PhimVang.Org.jpg', '<b>Ác quỷ Mouryou đã từng một thời gần như hủy diệt hoàn toàn thế giới, nay lại được hồi sinh một lần nữa. Để giải cứu thế giới khỏi bị hủy diệt, để phong ấn ác quỷ Mouryou chỉ có duy nhất một người làm được. Đó là nữ pháp sư Shion ở Quỷ Quốc với hai sức mạnh đặc biệt, một là phong ấn ác quỷ, hai là tiên đoán được cái chết của người khác. Nhiệm vụ lần này của Naruto là hộ tống bảo vệ nữ pháp sư, nhưng cô ấy lại tiên đoán rằng Naruto sẽ bị chết. Cách duy nhất để thoát được cái chết là phải tránh xa Shion. Thế nhưng khi rời bỏ&nbsp;</b>', '7', 724, 1, '6', 'Hajime Kamegaki', 'hajime kamegaki', '86 phút', '2007', '', 0, 0, 0, 0, 1, '', '1416774915', 0, 'Naruto Shippuuden The Movie1, Cái chết tiên đoán, Kizuna cái chết tiên đoán', 'naruto shippuuden the movie1, cai chet tien doan, kizuna cai chet tien doan', 0, NULL, 'HD', 0, NULL, NULL, NULL, '17', NULL, '', '', NULL, NULL, 1, '', 0, 20, 90, '', '0', '0', ''),
(835, 'Naruto Shippuuden The Movie 4: Ngọn Tháp Bị Mất', 'Naruto Shippuuden Movie 4: The Lost Tower', 'naruto shippuuden the movie 4: ngon thap bi mat', 'Junko Takeuchi, Toshiyuki Morikawa', 'junko takeuchi, toshiyuki morikawa', 'http://4.bp.blogspot.com/-J1MpX1384d8/UeYaJ9CxUCI/AAAAAAAANQg/jGmaEzXUGKw/s1600/Naruto+Shippuuden+Movie+4+The+Lost+Tower+(2010)_PhimVang.Org.jpg', '<b>Được giao một nhiệm vụ để bắt Mukade, một ninja phản bội, Naruto Uzumaki đến di tích lịch sử nổi tiếng là "Ouran". Mục tiêu Mukade được xuất hiện trong phế tích, hắn giải phóng sức mạnh được phong ấn bởi Hokage đệ tứ, gây ra một luồn ánh sáng bao lấy Naruto, gửi cậu vào quá khứ 20 năm về trước. Khi tỉnh dậy, Naruto gặp được Hokage thứ tư, Minato Namikaze.</b>', '7', 446, 1, '6', 'Masahiko Murata', 'masahiko murata', '86 phút', '2010', '', 1, 0, 0, 0, 1, '', '1416775011', 0, 'NaRuTo, Naruto Shippuuden, naruto shippuden, naruto shippuden movie 4 the lost tower, tòa tháp bị mất', 'naruto, naruto shippuuden, naruto shippuden, naruto shippuden movie 4 the lost tower, toa thap bi mat', 0, NULL, 'HD', 0, NULL, NULL, NULL, '17', NULL, '', '', NULL, NULL, 1, '', 0, 35, 115, '', '0', '0', ''),
